select name, count(*) as cnt from ((select leader_id, name from ((select leader_id from Gym) as t1 inner join (select tr.id as leader_id2, name from Trainer as tr) as t2 on t1.leader_id = t2.leader_id2)) as t3 inner join (select owner_id, pid from CatchedPokemon) as t4 on t3.leader_id = t4.owner_id) group by leader_id order by name;
