select name, temp1.sumlevel as sumlevel from ((select name, sum(level) as sumlevel from CatchedPokemon as cp1 inner join Trainer as t1 on cp1.owner_id = t1.id group by owner_id) as temp1 inner join (select sum(level) as sumlevel from CatchedPokemon as cp1 inner join Trainer as t1 on cp1.owner_id = t1.id group by owner_id order by sumlevel desc limit 1) as temp2 on temp1.sumlevel = temp2.sumlevel) order by name asc;
